function Main_Smoothness_SagittalLifting(file_input, output_folder)  [labels_Ankle, vector_Ankle] = SmoothnessAnkle_octave(file_input)close all[labels_Elbow, vector_Elbow] = SmoothnessElbow_octave(file_input)close all  [labels_Hip, vector_Hip] = SmoothnessHip_octave(file_input)close all  [labels_Knee, vector_Knee] = SmoothnessKnee_octave(file_input)close all [labels_Shoulder, vector_Shoulder] = SmoothnessShoulder_octave(file_input)close all [labels_Box, vector_Box] = SmoothnessBox_octave(file_input)close all   filename = strcat(output_folder, "/", "Smoothness_SagittalLifting", ".yaml");range_of_movement = store_vector_of_vector_result(filename, vector_Ankle, vector_Elbow, vector_Hip, ...                                                  vector_Knee, vector_Shoulder, vector_Box, ...                                                  labels_Ankle, labels_Elbow, labels_Hip, labels_Knee, labels_Shoulder, labels_Box);endfunction